import { useState, useEffect } from "react"

function App() {
  const [text, setText] = useState("")
  const [url, setUrl] = useState("")
  const [summary, setSummary] = useState("")
  const [sentencesCount, setSentencesCount] = useState(3)
  const [history, setHistory] = useState([])
  const [limit, setLimit] = useState(10)
  const [offset, setOffset] = useState(0)
  const [search, setSearch] = useState("")

  const handleSummarize = async (e) => {
    e.preventDefault()
    const body = { text: text.trim() || undefined, url: url.trim() || undefined, sentences_count: sentencesCount }
    try {
      const res = await fetch("http://127.0.0.1:8000/summarize", {
        method: "POST",
        headers: {"Content-Type": "application/json"},
        body: JSON.stringify(body)
      })
      if (!res.ok) throw new Error()
      const data = await res.json()
      setSummary(data.summary)
      setText("")
      setUrl("")
    } catch {}
  }

  const loadHistory = async () => {
    try {
      const params = new URLSearchParams({ limit, offset })
      if (search.trim()) params.append("search", search.trim())
      const res = await fetch("http://127.0.0.1:8000/history?" + params.toString())
      if (!res.ok) throw new Error()
      const data = await res.json()
      setHistory(data)
    } catch {}
  }

  const handleDeleteOne = async (id) => {
    try {
      const res = await fetch(`http://127.0.0.1:8000/history/${id}`, { method: "DELETE" })
      if (!res.ok) throw new Error()
      loadHistory()
    } catch {}
  }

  const handleDeleteAll = async () => {
    try {
      const res = await fetch("http://127.0.0.1:8000/history", { method: "DELETE" })
      if (!res.ok) throw new Error()
      loadHistory()
    } catch {}
  }

  const nextPage = () => {
    setOffset(offset + limit)
  }

  const prevPage = () => {
    setOffset(Math.max(offset - limit, 0))
  }

  useEffect(() => {
    loadHistory()
  }, [offset, limit])

  return (
    <div className="min-h-screen bg-gray-50 p-4 flex flex-col items-center">
      <h1 className="text-2xl font-bold mb-4">Text Summarizer</h1>
      <form onSubmit={handleSummarize} className="bg-white p-4 rounded shadow-md w-full max-w-md space-y-3">
        <textarea
          className="w-full p-2 border rounded"
          rows={4}
          placeholder="Text"
          value={text}
          onChange={(e) => setText(e.target.value)}
        />
        <input
          className="w-full p-2 border rounded"
          placeholder="URL"
          value={url}
          onChange={(e) => setUrl(e.target.value)}
        />
        <input
          className="w-full p-2 border rounded"
          type="number"
          min="1"
          max="10"
          value={sentencesCount}
          onChange={(e) => setSentencesCount(parseInt(e.target.value))}
        />
        <button className="bg-blue-600 text-white px-4 py-2 rounded w-full" type="submit">Summarize</button>
      </form>
      {summary && (
        <div className="bg-white p-4 rounded shadow-md w-full max-w-md mt-4">
          <h2 className="font-bold mb-2">Summary</h2>
          <p>{summary}</p>
        </div>
      )}
      <div className="bg-white p-4 rounded shadow-md w-full max-w-md mt-4 space-y-3">
        <div className="flex space-x-2">
          <input
            className="p-2 border rounded flex-1"
            placeholder="Search summaries"
            value={search}
            onChange={(e) => setSearch(e.target.value)}
          />
          <button onClick={() => { setOffset(0); loadHistory() }} className="bg-green-600 text-white px-4 py-2 rounded">Search</button>
        </div>
        <div className="flex space-x-2">
          <input
            className="p-2 border rounded w-1/2"
            type="number"
            min="1"
            value={limit}
            onChange={(e) => { setLimit(parseInt(e.target.value)); setOffset(0) }}
          />
          <button onClick={loadHistory} className="bg-gray-600 text-white px-4 py-2 rounded">Load</button>
          <button onClick={handleDeleteAll} className="bg-red-600 text-white px-4 py-2 rounded">Clear All</button>
        </div>
        <div className="flex justify-between">
          <button onClick={prevPage} className="bg-blue-600 text-white px-3 py-1 rounded">Prev</button>
          <button onClick={nextPage} className="bg-blue-600 text-white px-3 py-1 rounded">Next</button>
        </div>
        <ul className="space-y-2 mt-2">
          {history.map((item) => (
            <li key={item.id} className="border-b pb-2">
              <div className="font-bold">ID {item.id}</div>
              <div>{item.summary_text}</div>
              <div className="text-sm text-gray-500">{new Date(item.created_at).toLocaleString()}</div>
              <button onClick={() => handleDeleteOne(item.id)} className="bg-red-500 text-white px-2 py-1 rounded mt-1">Delete</button>
            </li>
          ))}
        </ul>
      </div>
    </div>
  )
}

export default App
