import { useState } from "react"

function App() {
  const [text, setText] = useState("")
  const [url, setUrl] = useState("")
  const [summary, setSummary] = useState("")
  const [sentencesCount, setSentencesCount] = useState(3)
  const [history, setHistory] = useState([])
  const [offset, setOffset] = useState(0)
  const [search, setSearch] = useState("")
  const [loading, setLoading] = useState(false)
  const [showHistory, setShowHistory] = useState(false)
  const limit = 5

  const handleSummarize = async (e) => {
    e.preventDefault()
    setLoading(true)
    try {
      const body = { text: text.trim() || undefined, url: url.trim() || undefined, sentences_count: sentencesCount }
      const res = await fetch("http://127.0.0.1:8000/summarize", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(body)
      })
      if (!res.ok) throw new Error()
      const data = await res.json()
      setSummary(data.summary)
      setText("")
      setUrl("")
    } catch {}
    setLoading(false)
  }

  const loadHistory = async (reset = false) => {
    setLoading(true)
    try {
      const newOffset = reset ? 0 : offset
      const params = new URLSearchParams({ limit, offset: newOffset })
      if (search.trim()) params.append("search", search.trim())
      const res = await fetch("http://127.0.0.1:8000/history?" + params.toString())
      if (!res.ok) throw new Error()
      const data = await res.json()
      if (reset) {
        setHistory(data)
        setOffset(limit)
      } else {
        setHistory((prev) => [...prev, ...data])
        setOffset((prev) => prev + limit)
      }
      setShowHistory(true)
    } catch {}
    setLoading(false)
  }

  const handleDeleteOne = async (id) => {
    setLoading(true)
    try {
      const res = await fetch(`http://127.0.0.1:8000/history/${id}`, { method: "DELETE" })
      if (!res.ok) throw new Error()
      setHistory((prev) => prev.filter((h) => h.id !== id))
    } catch {}
    setLoading(false)
  }

  const handleDeleteAll = async () => {
    setLoading(true)
    try {
      const res = await fetch("http://127.0.0.1:8000/history", { method: "DELETE" })
      if (!res.ok) throw new Error()
      setHistory([])
    } catch {}
    setLoading(false)
  }

  return (
    <div className="min-h-screen bg-black text-blue-300 p-4 flex flex-col items-center transition-all duration-300">
      <h1 className="text-3xl font-bold mb-8 animate-pulse">Text Summarizer</h1>
      <form onSubmit={handleSummarize} className="w-full max-w-md bg-gray-800 rounded p-4 shadow-lg space-y-4">
        <textarea
          className="w-full p-3 rounded border border-gray-600 focus:outline-none bg-gray-700 text-white"
          rows={4}
          placeholder="Text"
          value={text}
          onChange={(e) => setText(e.target.value)}
        />
        <input
          className="w-full p-3 rounded border border-gray-600 focus:outline-none bg-gray-700 text-white"
          placeholder="URL"
          value={url}
          onChange={(e) => setUrl(e.target.value)}
        />
        <input
          className="w-full p-3 rounded border border-gray-600 focus:outline-none bg-gray-700 text-white"
          type="number"
          min="1"
          max="10"
          value={sentencesCount}
          onChange={(e) => setSentencesCount(parseInt(e.target.value))}
        />
        <button
          className="w-full p-3 rounded bg-blue-600 text-white font-semibold hover:bg-blue-500 transition-colors duration-200"
          type="submit"
        >
          Summarize
        </button>
      </form>
      {loading && (
        <div className="mt-4 animate-spin rounded-full h-8 w-8 border-4 border-blue-400 border-t-transparent"></div>
      )}
      {summary && !loading && (
        <div className="w-full max-w-md bg-gray-800 rounded p-4 shadow-lg mt-6">
          <h2 className="font-bold mb-2 text-white">Summary</h2>
          <p className="text-blue-300">{summary}</p>
        </div>
      )}
      <div className="w-full max-w-md bg-gray-800 rounded p-4 shadow-lg mt-6 space-y-4">
        {!showHistory && (
          <button
            onClick={() => { loadHistory(true) }}
            className="w-full p-3 rounded bg-blue-600 text-white font-semibold hover:bg-blue-500 transition-colors duration-200"
          >
            Load History
          </button>
        )}
        {showHistory && (
          <div className="space-y-4">
            <div className="flex space-x-2">
              <input
                className="flex-1 p-3 rounded border border-gray-600 bg-gray-700 text-white focus:outline-none"
                placeholder="Search summaries"
                value={search}
                onChange={(e) => setSearch(e.target.value)}
              />
              <button
                onClick={() => { setHistory([]); loadHistory(true) }}
                className="p-3 rounded bg-green-600 text-white font-semibold hover:bg-green-500"
              >
                Search
              </button>
            </div>
            <button
              onClick={handleDeleteAll}
              className="w-full p-3 rounded bg-red-600 text-white font-semibold hover:bg-red-500"
            >
              Clear All
            </button>
            <div className="space-y-4">
              {history.map((item) => (
                <div
                  key={item.id}
                  className="border-b border-gray-600 pb-2 text-white animate-fadeIn"
                >
                  <div className="font-bold">ID {item.id}</div>
                  <div className="text-blue-300">{item.summary_text}</div>
                  <div className="text-sm text-gray-400">
                    {new Date(item.created_at).toLocaleString()}
                  </div>
                  <button
                    onClick={() => handleDeleteOne(item.id)}
                    className="mt-1 p-2 rounded bg-red-500 text-white font-semibold hover:bg-red-400"
                  >
                    Delete
                  </button>
                </div>
              ))}
            </div>
            {history.length > 0 && (
              <button
                onClick={() => loadHistory(false)}
                className="w-full p-3 rounded bg-blue-600 text-white font-semibold hover:bg-blue-500 transition-colors duration-200"
              >
                Load More
              </button>
            )}
          </div>
        )}
      </div>
    </div>
  )
}

export default App
